const Slider = () => {
  // Track the current view and selections
  const [currentView, setCurrentView] = useState("brands");
  const [selectedBrand, setSelectedBrand] = useState(null);
  const [selectedModel, setSelectedModel] = useState(null);
  const [selectedExpandedModel, setSelectedExpandedModel] = useState(null);
  const [selectedEngine, setSelectedEngine] = useState(null);
  const [selectedPartsType, setSelectedPartsType] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const [fuelTypeFilter, setFuelTypeFilter] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  // Handle navigation
  const handleBack = () => {
    switch (currentView) {
      case "models":
        setSelectedBrand(null);
        setCurrentView("brands");
        break;
      case "expandedModels":
        setSelectedModel(null);
        setCurrentView("models");
        break;
      case "engines":
        setSelectedExpandedModel(null);
        setCurrentView("expandedModels");
        break;
      case "autoPartsTypes":
        setSelectedEngine(null);
        setCurrentView("engines");
        break;
      case "subcategories":
        setSelectedPartsType(null);
        setCurrentView("autoPartsTypes");
        break;
      case "components":
        setSelectedSubcategory(null);
        setCurrentView("subcategories");
        break;
      default:
        break;
    }
  };

  // Update the EngineCard component to accept onClick prop
  const EngineCard = ({ specs, engine, onClick }) => (
    <div className="card engine-card" onClick={onClick}>
      <div className="engine-specs">
        <h3>{specs.engine}</h3>
        <p>Power: {specs.power}</p>
        <p>Year: {specs.year}</p>
        <p>Fuel: {specs.fuelType}</p>
        <p>Code: {specs.engineCode}</p>
      </div>
    </div>
  );

  // Filter buttons for fuel type
  const FilterButtons = () => (
    <div className="filter-buttons">
      <button
        className={`filter-btn gasoline ${
          fuelTypeFilter === "Gasoline" ? "active" : ""
        }`}
        onClick={() => setFuelTypeFilter("Gasoline")}
      >
        Gasoline
      </button>
      <button
        className={`filter-btn diesel ${
          fuelTypeFilter === "Diesel" ? "active" : ""
        }`}
        onClick={() => setFuelTypeFilter("Diesel")}
      >
        Diesel
      </button>
    </div>
  );

  // Add handleAddToCart function
  const handleAddToCart = (quantity) => {
    setCartCount((prev) => prev + quantity);
  };

  // Render content based on current view
  const renderContent = () => {
    switch (currentView) {
      case "brands":
        return brandsData.map((brand) => (
          <Card
            key={brand.id}
            image={brand.image}
            name={brand.name}
            onClick={() => {
              setSelectedBrand(brand);
              setCurrentView("models");
            }}
          />
        ));

      case "models":
        if (!selectedBrand) return null;
        return modelsData[selectedBrand.id].map((model) => (
          <Card
            key={model.id}
            image={model.image}
            name={model.name}
            onClick={() => {
              setSelectedModel(model);
              setCurrentView("expandedModels");
            }}
          />
        ));

      case "expandedModels":
        return (
          (selectedModel &&
            expandedModelsData[selectedModel.id]?.map((model) => (
              <Card
                key={model.id}
                image={model.image}
                name={model.name}
                onClick={() => {
                  setSelectedExpandedModel(model);
                  setCurrentView("engines");
                }}
              />
            ))) ||
          null
        );

      case "engines":
        if (!selectedExpandedModel) return null;

        const engines = engineData[selectedExpandedModel.id]?.filter(
          (engine) =>
            !fuelTypeFilter || engine.specs.fuelType === fuelTypeFilter
        );

        if (!engines) return null;

        return (
          <>
            <FilterButtons />
            <div className="engines-container">
              {engines.map((engine) => (
                <EngineCard
                  key={engine.id}
                  engine={engine}
                  specs={engine.specs}
                  onClick={() => {
                    setSelectedEngine(engine);
                    setCurrentView("autoPartsTypes");
                  }}
                />
              ))}
            </div>
          </>
        );

      case "autoPartsTypes":
        if (!selectedEngine) return null;
        return autoPartsCategories.map((category) => (
          <Card
            key={category.id}
            image={category.image}
            name={category.name}
            onClick={() => {
              setSelectedPartsType(category);
              setCurrentView("subcategories");
            }}
          />
        ));

      case "subcategories":
        if (!selectedPartsType) return null;
        return autoPartsData[selectedPartsType.id].map((subcategory) => (
          <Card
            key={subcategory.id}
            image={subcategory.image}
            name={subcategory.name}
            onClick={() => {
              setSelectedSubcategory(subcategory);
              setCurrentView("components");
            }}
          />
        ));

      case "components":
        if (!selectedSubcategory) return null;
        const parts = partsData[selectedSubcategory.id];
        if (!parts) return null;

        return (
          <div className="components-container">
            {parts.map((part) => (
              <ProductCard
                key={part.id}
                product={{
                  ...part,
                  onAddToCart: handleAddToCart,
                }}
              />
            ))}
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="slider-container">
      {currentView !== "brands" && (
        <button
          className="nav-arrow left"
          onClick={handleBack}
          style={{
            visibility: currentView !== "brands" ? "visible" : "hidden",
          }}
        >
          <i className="fas fa-chevron-left"></i>
        </button>
      )}

      <div
        className={`slider ${
          currentView !== "brands" ? "horizontal-scroll" : ""
        }`}
      >
        {renderContent()}
      </div>
    </div>
  );
};

export default Slider;
