import React, { useState } from "react";
import Card from "../Card/Card";
import "./Slider.css";

const brandsData = [
  {
    id: "audi",
    name: "Audi",
    image: "images/brands/audi.png",
    models: [
      {
        id: "audi-80",
        name: "Audi 80",
        image: "images/models/audi80.png",
      },

      {
        id: "audi-100",
        name: "Audi 100",
        image: "images/models/audi100.png",
      },
      {
        id: "audi-a3",
        name: "Audi A3",
        image: "images/models/audiA3.png",
      },
    ],
  },
  // Add more brands here
];

const Slider = () => {
  const [selectedBrand, setSelectedBrand] = useState(null);

  const handleBrandClick = (brand) => {
    setSelectedBrand(brand);
  };

  const handleBack = () => {
    setSelectedBrand(null);
  };

  return (
    <div className="slider-container">
      <button
        className="nav-arrow left"
        onClick={handleBack}
        style={{ visibility: selectedBrand ? "visible" : "hidden" }}
      >
        <i className="fas fa-chevron-left"></i>
      </button>

      <div className="slider">
        {!selectedBrand
          ? // Show brands
            brandsData.map((brand) => (
              <Card
                key={brand.id}
                image={brand.image}
                name={brand.name}
                onClick={() => handleBrandClick(brand)}
              />
            ))
          : // Show models
            selectedBrand.models.map((model) => (
              <Card
                key={model.id}
                image={model.image}
                name={model.name}
                onClick={() => {
                  /* Handle model selection */
                }}
              />
            ))}
      </div>
    </div>
  );
};

export default Slider;
