import React, { useState } from "react";
import Card from "../Card/Card";
import "./Slider.css";
import ProductCard from "../ProductCard/ProductCard";

// Create a single comprehensive data structure
const carData = [
  {
    id: "audi",
    name: "Audi",
    image: "images/brands/audi.png",
    models: [
      {
        id: "audi-80",
        name: "Audi 80",
        image: "images/models/audi-80.jpg",
        variants: [
          {
            id: "audi-80-b2",
            name: "Audi 80 B2",
            image: "images/models/expanded/audi-80-b2.jpg",
            engines: [
              {
                id: "eng-1",
                name: "1.6",
                specs: {
                  engine: "1.6L I4",
                  power: "75kW/102HP",
                  year: "1978-1984",
                  fuelType: "Gasoline",
                  engineCode: "YN",
                  parts: {
                    transmission: [
                      {
                        id: "flywheel-1",
                        name: "Flywheel",
                        image: "images/parts/transmission/flywheel-1.jpg",
                        price: 299.99,
                        additionalImages: [
                          /* ... */
                        ],
                      },
                      {
                        id: "clutch-1",
                        name: "Clutch Kit",
                        image: "images/parts/transmission/clutch-1.jpg",
                        price: 199.99,
                        additionalImages: [
                          /* ... */
                        ],
                      },
                    ],
                    accessories: [
                      {
                        id: "alarm-1",
                        name: "Car Alarm System",
                        image: "images/parts/accessories/alarm-1.jpg",
                        price: 149.99,
                        additionalImages: [
                          /* ... */
                        ],
                      },
                    ],
                  },
                },
              },
              {
                id: "eng-2",
                name: "1.6D",
                specs: {
                  engine: "1.6L I4",
                  power: "54kW/73HP",
                  year: "1980-1984",
                  fuelType: "Diesel",
                  engineCode: "CR",
                  parts: {
                    /* Similar structure */
                  },
                },
              },
            ],
          },
          // Other variants (B3, B4)
        ],
      },
      // Other models (100, A3)
    ],
  },
  {
    id: "bmw",
    name: "BMW",
    image: "images/brands/bmw.png",
    models: [
      {
        id: "bmw-3",
        name: "3 Series",
        image: "images/models/bmw-3.jpg",
        variants: [
          {
            id: "bmw-3-e30",
            name: "3 Series E30",
            image: "images/models/expanded/bmw-3-e30.jpg",
            engines: [
              /* Similar structure */
            ],
          },
          // Other variants (E36, E46)
        ],
      },
      // Other models (5 Series, 7 Series)
    ],
  },
  // Other brands...
];

const Slider = () => {
  const [selectedBrand, setSelectedBrand] = useState(null);
  const [selectedModel, setSelectedModel] = useState(null);
  const [selectedVariant, setSelectedVariant] = useState(null);
  const [selectedEngine, setSelectedEngine] = useState(null);
  const [currentView, setCurrentView] = useState("brands");
  const [fuelTypeFilter, setFuelTypeFilter] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  // Update handleBack to handle all views correctly
  const handleBack = () => {
    switch (currentView) {
      case "models":
        setSelectedBrand(null);
        setCurrentView("brands");
        break;
      case "variants":
        setSelectedModel(null);
        setCurrentView("models");
        break;
      case "engines":
        setSelectedVariant(null);
        setCurrentView("variants");
        break;
      case "autoPartsTypes":
        setSelectedEngine(null);
        setCurrentView("engines");
        break;
      case "components":
        setCurrentView("autoPartsTypes");
        break;
      default:
        break;
    }
  };

  // Update the EngineCard component to accept onClick prop
  const EngineCard = ({ specs, engine, onClick }) => (
    <div className="card engine-card" onClick={onClick}>
      <div className="engine-specs">
        <h3>{specs.engine}</h3>
        <p>Power: {specs.power}</p>
        <p>Year: {specs.year}</p>
        <p>Fuel: {specs.fuelType}</p>
        <p>Code: {specs.engineCode}</p>
      </div>
    </div>
  );

  // Filter buttons for fuel type
  const FilterButtons = () => (
    <div className="filter-buttons">
      <button
        className={`filter-btn gasoline ${
          fuelTypeFilter === "Gasoline" ? "active" : ""
        }`}
        onClick={() => setFuelTypeFilter("Gasoline")}
      >
        Gasoline
      </button>
      <button
        className={`filter-btn diesel ${
          fuelTypeFilter === "Diesel" ? "active" : ""
        }`}
        onClick={() => setFuelTypeFilter("Diesel")}
      >
        Diesel
      </button>
    </div>
  );

  // Add handleAddToCart function
  const handleAddToCart = (quantity) => {
    setCartCount((prev) => prev + quantity);
  };

  // Update renderContent to work with the new data structure
  const renderContent = () => {
    switch (currentView) {
      case "brands":
        return carData.map((brand) => (
          <Card
            key={brand.id}
            image={brand.image}
            name={brand.name}
            onClick={() => {
              setSelectedBrand(brand);
              setCurrentView("models");
            }}
          />
        ));

      case "models":
        return selectedBrand.models.map((model) => (
          <Card
            key={model.id}
            image={model.image}
            name={model.name}
            onClick={() => {
              setSelectedModel(model);
              setCurrentView("variants");
            }}
          />
        ));

      case "variants":
        return selectedModel.variants.map((variant) => (
          <Card
            key={variant.id}
            image={variant.image}
            name={variant.name}
            onClick={() => {
              setSelectedVariant(variant);
              setCurrentView("engines");
            }}
          />
        ));

      case "engines":
        if (!selectedVariant?.engines) return null;
        const engines = selectedVariant.engines.filter(
          (engine) =>
            !fuelTypeFilter || engine.specs.fuelType === fuelTypeFilter
        );
        return (
          <>
            <FilterButtons />
            <div className="engines-container">
              {engines.map((engine) => (
                <EngineCard
                  key={engine.id}
                  engine={engine}
                  specs={engine.specs}
                  onClick={() => {
                    setSelectedEngine(engine);
                    setCurrentView("autoPartsTypes");
                  }}
                />
              ))}
            </div>
          </>
        );

      case "autoPartsTypes":
        if (!selectedEngine?.specs?.parts) return null;
        return (
          <div className="parts-types-container">
            {Object.entries(selectedEngine.specs.parts).map(([type, parts]) => (
              <Card
                key={type}
                image={`images/parts/${type}.jpg`}
                name={type.charAt(0).toUpperCase() + type.slice(1)}
                onClick={() => {
                  setCurrentView("components");
                }}
              />
            ))}
          </div>
        );

      case "components":
        if (!selectedEngine?.specs?.parts) return null;
        return (
          <div className="components-container">
            {Object.entries(selectedEngine.specs.parts).map(([type, parts]) => (
              <div key={type} className="parts-section">
                <h3 className="parts-section-title">
                  {type.charAt(0).toUpperCase() + type.slice(1)}
                </h3>
                <div className="parts-list">
                  {parts.map((part) => (
                    <ProductCard
                      key={part.id}
                      product={{
                        ...part,
                        additionalImages: [part.image, part.image, part.image],
                        onAddToCart: handleAddToCart,
                      }}
                    />
                  ))}
                </div>
              </div>
            ))}
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="slider-container">
      {currentView !== "brands" && (
        <button
          className="nav-arrow left"
          onClick={handleBack}
          style={{
            visibility: currentView !== "brands" ? "visible" : "hidden",
          }}
        >
          <i className="fas fa-chevron-left"></i>
        </button>
      )}

      <div
        className={`slider ${
          currentView !== "brands" ? "horizontal-scroll" : ""
        }`}
      >
        {renderContent()}
      </div>
    </div>
  );
};

export default Slider;
