import React, { useState } from "react";
import Card from "../Card/Card";
import "./Slider.css";

// Expanded models data
const expandedModelsData = {
  "audi-80": [
    {
      id: "audi-80-b3",
      name: "Audi 80 B3",
      image: "images/models/expanded/audi-80-b3.jpg",
      engines: [
        {
          id: "eng-1",
          name: "1.8",
          specs: {
            engine: "1.8L I4",
            power: "75kW/102HP",
            year: "1986-1991",
            fuelType: "Gasoline",
            engineCode: "PM",
          },
        },
        {
          id: "eng-2",
          name: "1.6D",
          specs: {
            engine: "1.6L I4",
            power: "54kW/73HP",
            year: "1986-1991",
            fuelType: "Diesel",
            engineCode: "RA",
          },
        },
      ],
    },
    // Add more expanded models...
  ],
};

// Auto parts categories
const autoPartsTypes = [
  {
    id: "transmission",
    name: "Transmission",
    image: "images/parts/transmission.jpg",
    components: [
      {
        id: "flywheel",
        name: "Flywheel",
        image: "images/parts/flywheel.jpg",
      },
      {
        id: "clutch",
        name: "Clutch",
        image: "images/parts/clutch.jpg",
      },
    ],
  },
  {
    id: "accessories",
    name: "Auto Accessories",
    image: "images/parts/accessories.jpg",
    components: [
      {
        id: "alarms",
        name: "Car Alarms",
        image: "images/parts/alarms.jpg",
      },
      {
        id: "cosmetics",
        name: "Car Cosmetics",
        image: "images/parts/cosmetics.jpg",
      },
    ],
  },
];

// Original brandsData remains the same
const brandsData = [
  {
    id: "audi",
    name: "Audi",
    image: "images/brands/audi.png",
    models: [
      {
        id: "audi-80",
        name: "Audi 80",
        image: "images/models/audi-80.jpg",
      },
      {
        id: "audi-100",
        name: "Audi 100",
        image: "images/models/audi-100.png",
      },
      {
        id: "audi-a3",
        name: "Audi A3",
        image: "images/models/audi-a3.jpg",
      },
    ],
  },
  // Add more brands here
];

const Slider = () => {
  // Track the current view and selections
  const [currentView, setCurrentView] = useState("brands");
  const [selectedBrand, setSelectedBrand] = useState(null);
  const [selectedModel, setSelectedModel] = useState(null);
  const [selectedExpandedModel, setSelectedExpandedModel] = useState(null);
  const [selectedEngine, setSelectedEngine] = useState(null);
  const [fuelTypeFilter, setFuelTypeFilter] = useState(null);

  // Handle navigation
  const handleBack = () => {
    switch (currentView) {
      case "models":
        setSelectedBrand(null);
        setCurrentView("brands");
        break;
      case "expandedModels":
        setSelectedModel(null);
        setCurrentView("models");
        break;
      case "engines":
        setSelectedExpandedModel(null);
        setCurrentView("expandedModels");
        break;
      case "parts":
        setSelectedEngine(null);
        setCurrentView("engines");
        break;
      default:
        break;
    }
  };

  // Render engine specification card
  const EngineCard = ({ specs }) => (
    <div className="card engine-card">
      <div className="engine-specs">
        <h3>{specs.engine}</h3>
        <p>Power: {specs.power}</p>
        <p>Year: {specs.year}</p>
        <p>Fuel: {specs.fuelType}</p>
        <p>Code: {specs.engineCode}</p>
      </div>
    </div>
  );

  // Filter buttons for fuel type
  const FilterButtons = () => (
    <div className="filter-buttons">
      <button
        className={`filter-btn gasoline ${
          fuelTypeFilter === "Gasoline" ? "active" : ""
        }`}
        onClick={() => setFuelTypeFilter("Gasoline")}
      >
        Gasoline
      </button>
      <button
        className={`filter-btn diesel ${
          fuelTypeFilter === "Diesel" ? "active" : ""
        }`}
        onClick={() => setFuelTypeFilter("Diesel")}
      >
        Diesel
      </button>
    </div>
  );

  // Render content based on current view
  const renderContent = () => {
    switch (currentView) {
      case "brands":
        return brandsData.map((brand) => (
          <Card
            key={brand.id}
            image={brand.image}
            name={brand.name}
            onClick={() => {
              setSelectedBrand(brand);
              setCurrentView("models");
            }}
          />
        ));

      case "models":
        return selectedBrand.models.map((model) => (
          <Card
            key={model.id}
            image={model.image}
            name={model.name}
            onClick={() => {
              setSelectedModel(model);
              setCurrentView("expandedModels");
            }}
          />
        ));

      case "expandedModels":
        return expandedModelsData[selectedModel.id].map((model) => (
          <Card
            key={model.id}
            image={model.image}
            name={model.name}
            onClick={() => {
              setSelectedExpandedModel(model);
              setCurrentView("engines");
            }}
          />
        ));

      case "engines":
        const engines = selectedExpandedModel.engines.filter(
          (engine) =>
            !fuelTypeFilter || engine.specs.fuelType === fuelTypeFilter
        );
        return (
          <>
            <FilterButtons />
            <div className="engines-container">
              {engines.map((engine) => (
                <EngineCard key={engine.id} specs={engine.specs} />
              ))}
            </div>
          </>
        );

      case "parts":
        return autoPartsTypes.map((part) => (
          <Card
            key={part.id}
            image={part.image}
            name={part.name}
            onClick={() => {
              // Handle parts selection
            }}
          />
        ));

      default:
        return null;
    }
  };

  return (
    <div className="slider-container">
      <button
        className="nav-arrow left"
        onClick={handleBack}
        style={{ visibility: currentView !== "brands" ? "visible" : "hidden" }}
      >
        <i className="fas fa-chevron-left"></i>
      </button>

      <div className="slider">{renderContent()}</div>
    </div>
  );
};

export default Slider;
