import React, { useState } from "react";
import Card from "../Card/Card";
import "./Slider.css";
import ProductCard from "../ProductCard/ProductCard";

const carData = [
  {
    id: "audi",
    name: "Audi",
    image: "images/brands/audi.png",
    models: [
      {
        id: "audi-80",
        name: "Audi 80",
        image: "images/models/audi-80.jpg",
      },
      {
        id: "audi-100",
        name: "Audi 100",
        image: "images/models/audi-100.jpg",
      },
      {
        id: "audi-a3",
        name: "Audi A3",
        image: "images/models/audi-a3.jpg",
      },
    ],
  },
  {
    id: "bmw",
    name: "BMW",
    image: "images/brands/bmw.png",
    models: [
      /* BMW models */
    ],
  },
  // Add other brands here
];

// Expanded models data
const expandedModelsData = {
  "audi-80": [
    {
      id: "audi-80-b2",
      name: "Audi 80 B2",
      image: "images/models/expanded/audi-80-b2.jpg",
      engines: [
        {
          id: "eng-1",
          name: "1.6",
          specs: {
            engine: "1.6L I4",
            power: "75kW/102HP",
            year: "1978-1984",
            fuelType: "Gasoline",
            engineCode: "YN",
          },
        },
        {
          id: "eng-2",
          name: "1.6D",
          specs: {
            engine: "1.6L I4",
            power: "54kW/73HP",
            year: "1980-1984",
            fuelType: "Diesel",
            engineCode: "CR",
          },
        },
      ],
    },
  ],
};

// Auto parts categories
const autoPartsTypes = [
  {
    id: "transmission",
    name: "Transmission",
    image: "images/parts/transmission.jpg",
    components: [
      {
        id: "flywheel",
        name: "Flywheel",
        image: "images/parts/flywheel.jpg",
        price: 299.99,
      },
      {
        id: "clutch",
        name: "Clutch",
        image: "images/parts/clutch.jpg",
        price: 199.99,
      },
    ],
  },
  {
    id: "accessories",
    name: "Auto Accessories",
    image: "images/parts/accessories.jpg",
    components: [
      {
        id: "alarms",
        name: "Car Alarms",
        image: "images/parts/alarms.jpg",
        price: 149.99,
      },
      {
        id: "cosmetics",
        name: "Car Cosmetics",
        image: "images/parts/cosmetics.jpg",
        price: 99.99,
      },
    ],
  },
];

const Slider = () => {
  const [selectedBrand, setSelectedBrand] = useState(null);
  const [selectedModel, setSelectedModel] = useState(null);
  const [selectedExpandedModel, setSelectedExpandedModel] = useState(null);
  const [selectedEngine, setSelectedEngine] = useState(null);
  const [selectedPartsType, setSelectedPartsType] = useState(null);
  const [currentView, setCurrentView] = useState("brands");
  const [fuelTypeFilter, setFuelTypeFilter] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  const handleBack = () => {
    switch (currentView) {
      case "models":
        setSelectedBrand(null);
        setCurrentView("brands");
        break;
      case "expandedModels":
        setSelectedModel(null);
        setCurrentView("models");
        break;
      case "engines":
        setSelectedExpandedModel(null);
        setCurrentView("expandedModels");
        break;
      case "autoPartsTypes":
        setSelectedEngine(null);
        setCurrentView("engines");
        break;
      case "components":
        setSelectedPartsType(null);
        setCurrentView("autoPartsTypes");
        break;
      default:
        break;
    }
  };

  const EngineCard = ({ specs, onClick }) => (
    <div className="card engine-card" onClick={onClick}>
      <div className="engine-specs">
        <h3>{specs.engine}</h3>
        <p>Power: {specs.power}</p>
        <p>Year: {specs.year}</p>
        <p>Fuel: {specs.fuelType}</p>
        <p>Code: {specs.engineCode}</p>
      </div>
    </div>
  );

  const FilterButtons = () => (
    <div className="filter-buttons">
      <button
        className={`filter-btn gasoline ${
          fuelTypeFilter === "Gasoline" ? "active" : ""
        }`}
        onClick={() => setFuelTypeFilter("Gasoline")}
      >
        Gasoline
      </button>
      <button
        className={`filter-btn diesel ${
          fuelTypeFilter === "Diesel" ? "active" : ""
        }`}
        onClick={() => setFuelTypeFilter("Diesel")}
      >
        Diesel
      </button>
    </div>
  );

  const handleAddToCart = (quantity) => {
    setCartCount((prev) => prev + quantity);
  };

  const renderContent = () => {
    switch (currentView) {
      case "brands":
        return carData.map((brand) => (
          <Card
            key={brand.id}
            image={brand.image}
            name={brand.name}
            onClick={() => {
              setSelectedBrand(brand);
              setCurrentView("models");
            }}
          />
        ));

      case "models":
        return selectedBrand?.models.map((model) => (
          <Card
            key={model.id}
            image={model.image}
            name={model.name}
            onClick={() => {
              setSelectedModel(model);
              setCurrentView("expandedModels");
            }}
          />
        ));

      case "expandedModels":
        return expandedModelsData[selectedModel?.id]?.map((model) => (
          <Card
            key={model.id}
            image={model.image}
            name={model.name}
            onClick={() => {
              setSelectedExpandedModel(model);
              setCurrentView("engines");
            }}
          />
        ));

      case "engines":
        if (!selectedExpandedModel?.engines) return null;
        const engines = selectedExpandedModel.engines.filter(
          (engine) =>
            !fuelTypeFilter || engine.specs.fuelType === fuelTypeFilter
        );
        return (
          <>
            <FilterButtons />
            <div className="engines-container">
              {engines.map((engine) => (
                <EngineCard
                  key={engine.id}
                  specs={engine.specs}
                  onClick={() => {
                    setSelectedEngine(engine);
                    setCurrentView("autoPartsTypes");
                  }}
                />
              ))}
            </div>
          </>
        );

      case "autoPartsTypes":
        return autoPartsTypes.map((type) => (
          <Card
            key={type.id}
            image={type.image}
            name={type.name}
            onClick={() => {
              setSelectedPartsType(type);
              setCurrentView("components");
            }}
          />
        ));

      case "components":
        if (!selectedPartsType) return null;
        return (
          <div className="components-container">
            {selectedPartsType.components.map((component) => (
              <ProductCard
                key={component.id}
                product={{
                  ...component,
                  additionalImages: [
                    component.image,
                    component.image,
                    component.image,
                  ],
                  onAddToCart: handleAddToCart,
                }}
              />
            ))}
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="slider-container">
      {currentView !== "brands" && (
        <button
          className="nav-arrow left"
          onClick={handleBack}
          style={{
            visibility: currentView !== "brands" ? "visible" : "hidden",
          }}
        >
          <i className="fas fa-chevron-left"></i>
        </button>
      )}

      <div
        className={`slider ${
          currentView !== "brands" ? "horizontal-scroll" : ""
        }`}
      >
        {renderContent()}
      </div>
    </div>
  );
};

export default Slider;
